import React, {useState} from "react";
import {useDispatch} from "react-redux";

const WhatsHappening = () => {
  let [whatsHappening, setWhatsHappening] = useState('');
  const dispatch = useDispatch();
  const tuitClickHandler = () => {
    dispatch({type: 'create-tuit',
      tuit: whatsHappening
    });
    console.log(whatsHappening);
  }
  return (
      <>
        <div className="pt-2 row">
          <div className="col-1">
            <img className="wd-rounded-corners-all-around" height="50" width="50" src=""/>
          </div>
          <div className="col-11">
            <textarea value={whatsHappening}
                      onChange={(event) =>
                          setWhatsHappening(event.target.value)}>
            </textarea>

            <i className="ms-3 me-2 fa-solid fa-image"></i>
            <i className="m-2 fa-solid fa-arrow-trend-up"></i>
            <i className="m-2  fa-solid fa-face-smile"></i>
            <i className="pb-3 m-2 fa-solid fa-calendar"></i>


            <button onClick={tuitClickHandler}>
              Tuit
            </button>
          </div>
        </div>


      </>
  );
}
export default WhatsHappening;
