import React, {useState} from "react";
import {useDispatch,useSelector} from "react-redux";

const Todos = () =>{
  const todos = useSelector(state => state.todos);
  const [todo, setTodo] = useState({do: ''});
  const dispatch = useDispatch();
  const createTodoClickHandler = () =>{
    const action = {
      type: 'createtodo',todo
    };
    dispatch(action);
  }
  const deleteTodoClickHandler = (todo) =>{
    const action = {
      type: 'deletetodo',todo
    };
  }
  const todoChangeHandler = (event) =>{
    const doValue = event.target.value;
    const newTodo = {
      do: doValue
    };
    setTodo(newTodo);
  }
  return(
      <>
        <h3>Todos</h3>
        <ul className="list-group">
          {
            todos.map(todo => <li className="list-group-item">
              <input onChange ={todoChangeHandler}
              value = {todo.do}
              className="form-control"/>
              <button onClick={createTodoClickHandler} className="btn btn-primary">
                Create New Todo
              </button>
            </li>)
          }
        </ul>
      </>

  );
};

export default Todos;
